class LocalStorageUtil{constructor(){this.keyName="products"}getProducts(){const t=localStorage.getItem(this.keyName);return null!=t?JSON.parse(t):[]}putProducts(t){let e=this.getProducts(),o=!1;const r=e.indexOf(t);return-1===r?(e.push(t),o=!0):e.splice(r,1),localStorage.setItem(this.keyName,JSON.stringify(e)),{pushProduct:o,products:e}}}export const localStorageCart=new LocalStorageUtil;