import{productsList}from"./COMPONENTS/products.js";import{localStorageCart}from"./COMPONENTS/localStorage.js";import{cart}from"./COMPONENTS/cart.js";import{DATA}from"./COMPONENTS/Data.js";productsList.render(),cart.render();const menuList=document.querySelector("ul.burgerMenu"),btn=document.querySelector(".burger-menu");addEventListener("click",t=>{t.target.closest("div.burger-menu")&&(btn.classList.contains("active")?(btn.classList.remove("active"),menuList.classList.remove("active")):(btn.classList.add("active"),menuList.classList.add("active")))});const cartItem=document.querySelector(".cart__text");cartItem.innerHTML=`Cart (${localStorageCart.getProducts().length})`,cartItem.addEventListener("click",showCart);let closeCartBtn=document.querySelector(".closeCart");function closeCart(){document.querySelector(".openCart").classList.remove("show")}function showCart(){let t=document.querySelector(".openCart");t.classList.add("show");let e=t.querySelector("ul");e.innerHTML="",fillCart(e)}function fillCart(t){let e="",r=0;localStorageCart.getProducts().forEach(t=>{DATA.forEach(c=>{c.id==t&&(e+=`\n                <li>${localStorageCart.getProducts().indexOf(t)+1}. ${c.name}: ${c.price}</li>\n                `,r+=+c.price)})}),document.querySelector("span.sum").innerHTML=r+" $",t.innerHTML=e}closeCartBtn.addEventListener("click",closeCart);const btns=document.querySelectorAll(".order");function selectOrder(){const{pushProduct:t,products:e}=localStorageCart.putProducts(this.dataset.id);t?(this.classList.add("active"),this.innerHTML="Remove"):(this.classList.remove("active"),this.innerHTML="Add to cart"),cartItem.innerHTML=`Cart (${localStorageCart.getProducts().length})`}btns.forEach(t=>{t.addEventListener("click",selectOrder)});